import { AsyncAPI } from "./types";

type SourceType = "album" | "camera";

type SizeType = "original" | "compressed";

type TempFile = {
  /** 本地文件路径 */
  path: string;
  /** 本地文件大小，以字节为单位 */
  size: number;
};
/**
 * ### 从系统相册中选择图片，或使用相机拍摄图片，选取成功后将存入本地临时文件，并在 success 回调中返回相应路径列表
 * 该 API 需要用户授权方可调用，详细信息可参考[用户授权](https://developer.open-douyin.com/docs/resource/zh-CN/mini-app/develop/api/other/user-authorization/)，
 * 可在 [tt.authorize](https://developer.open-douyin.com/docs/resource/zh-CN/mini-app/develop/api/open-interface/authorization/tt-authorize/) 中查看相关错误码信息。
 */
export const chooseImage: AsyncAPI<
  {
    /** 最多可以选择的图片数量，拍照时此选项无效 */
    count: number;
    /** 指定图片来源。album：从相册选择图片；camera：使用相机拍摄图片；album & camera：既可以从相册选择图片，也可以使用相机拍摄图片；*/
    sourceType: SourceType[];
    /** 是否选择原图。original：使用原图，显示并默认勾选原图按钮；compressed：使用压缩图，不显示原图按钮；original & compressed：提供原图按钮，可自由选择是否使用原图； */
    sizeType: SizeType[];
  },
  {
    tempFilePaths: string;
    tempFiles: TempFile[];
  }
>;
